function Error = analogWrite(ioPin, V)% Inputs: ioPin, V (voltage value)% Outputs: Error% ioPin options: DAC0: 0; DAC1: 1%Initialize LJUD Constant LJ_ioPUT_DACglobal ljHandleglobal LJ_ioPUT_DAC% Send voltage V to Analog pinif length(V) == length(ioPin)  for i = 1:length(ioPin)    Error = ljud_ePut (ljHandle, LJ_ioPUT_DAC, ioPin(i), V(i), 0);  endelseif length(V) == 1  for i = 1:length(ioPin)    Error = ljud_ePut (ljHandle, LJ_ioPUT_DAC, ioPin(i), V, 0);  endelse  Error = "The V vector size must equal 1 or be the same as the length of the ioPin vector."end