function clickStates = crossWalkButtons()% This script reads the each button continuously for a button press% and outputs the activation state for North-South, and East-West crosswalkst = gtime();global readyEW % Declares readyEW and readyNS as global (should be initialized as zero elsewhere)global readyNSclickStates = [~digitalRead(0), ~digitalRead(1)]; clickNS = clickStates(1);clickEW = clickStates(2);if (t <= 21.1) || t > 42  if clickEW == 1; % A digitalRead of 0 means it was clicked.    readyEW = 1;  endelse  readyEW = 0;endif (t < 0.1) || (t > 12 && t <= 51)  if clickNS == 1;    readyNS = 1;  endelse  readyNS = 0;endend % function end