function clickStates = crossWalkButtonsB()% This script reads the each button continuously for a button press% and outputs the activation state for North-South, and East-West crosswalksglobal readyEW % Declares readyEW and readyNS as global (should be initialized as zero elsewhere)global readyNS%clickStates = [digitalRead(0), digitalRead(1)]; [Error clickNS] = digitalRead(0);[Error clickEW] = digitalRead(1);clickStates = [clickNS, clickEW];if clickEW == 0; % A digitalRead of 0 means it was clicked.  readyEW = 1;endif clickNS == 0;  readyNS = 1;endend % function end