function cwStates = crossWalkLights()% This script activates the cross walk sequence and takes inputs from the% crossWalkButtons script.% Inputs: None% Outputs: cwStates% Call global time.t = gtime();% Walk and Stop Valuesw = [1 0]; % walk signs = [0 1]; % stop (hand) signb = [0 0]; % both off (for blinking)% example: [nsCW, ewCW] --> [w, s]: ns is walk, ew is stop.% Timing sequenceif t < 12  % NS crosswalk on  cwStates = [w s]; % Outputs CW state values for diagnostic script.  digitalWrite(2:5, cwStates);elseif t < 21  % blink the NS stop light on even time counts  if mod(round(t),2) == 0    cwStates = [s s];    digitalWrite(2:5,cwStates);  else    cwStates = [b s];    digitalWrite(2:5,cwStates);  endelseif t < 42  % EW crosswalk on  cwStates = [s w];  digitalWrite(2:5,cwStates);elseif t < 51  % blink the EW crosswalk  if mod(round(t),2) == 0    cwStates = [s s];    digitalWrite(2:5,cwStates);  else    cwStates = [s b];    digitalWrite(2:5,cwStates);  end% Already included in TrafficLights()  %else%cwStates = [b b];%gtreset();end