function cwStates = crossWalkLights()% Has subfunction crossWalkButtons()% This script activates the cross walk sequence and takes inputs from the% crossWalkButtons script.% Call global time.t = gtime();% Walk and Stop Valuesw = [0 1]; % walk signs = [1 0]; % stop (hand) signb = [0 0]; % both off (for blinking)% example: [ewCW, nsCW] --> [w, s]: ew is walk, ns is stop.% Timing sequenceif t < 12% NS crosswalk oncwStates = [s w]; % Outputs CW state values for diagnostic script.digitalWrite(2:5, cwStates);elseif t < 21% blink the NS stop light  if mod(round(t),2) == 0    cwStates = [s s];    digitalWrite(2:5,cwStates);  else    cwStates = [s b];    digitalWrite(2:5,cwStates);  endelseif t < 42% EW crosswalk oncwStates = [w s];digitalWrite(2:5,cwStates);elseif t < 51% blink the EW crosswalk  if mod(round(t),2) == 0    cwStates = [s s];    digitalWrite(2:5,cwStates);  else    cwStates = [b s];    digitalWrite(2:5,cwStates);  endelsecwStates = [b b];gtreset();end