function Error = digitalWrite(ioPin, data)% Writes digital data to specified IO pin.% Inputs: ioPin, data% Outputs: Error% Initialize LJ (global?) Constantsglobal ljHandleglobal LJ_ioPUT_DIGITAL_BIT% Write data to LabJackioPin = uint8(ioPin);data = logical(data);% Can write arrays of pins and data to outputs.if length(data) > 1  for i = 1:length(ioPin)    Error = ljud_ePut(ljHandle,LJ_ioPUT_DIGITAL_BIT,ioPin(i),data(i),0);  endelseif length(data) == 1  for i = 1:length(ioPin)    Error = ljud_ePut(ljHandle,LJ_ioPUT_DIGITAL_BIT,ioPin(i),data,0);  endelse  Error = "Data input array size must be 1 or equal the length of ioPin array"end