% Default driver for scenario B. Works at 1X speed and outputs button, turn lane% and ambient light statuses.clearclcinitializeAll(1);global readyNSglobal readyEWwhile truet = gtime();trafficArray = trafficLightsB();carSense = turnLanesB();cwStates = crossWalkLightsB();clickStates = crossWalkButtonsB();lux = ambientLight();if abs(t - round(t)) < 0.01  fprintf('Time: %i s.\n', round(t));  fprintf('CW-NS: %i. CW-EW: %i.\n', [readyNS, readyEW]);  fprintf('CarTLA: %i. CarTLC: %i.\n', ~carSense(1), ~carSense(2));  fprintf('Lux: %f\n\n', lux);endend