function initializeAll(s)% Initializes LabJack, all scripts and all global variables.% Inputs: s (speed)global speed;global TLAseq;global TLCseq;global readyEW;global readyNS;global blink;global flickerTime;global flickerValue;blink = 0; % blink falseflickerTime = 0;flickerValue = 0;readyEW = 0; % Initialize global button states.readyNS = 0;TLAseq = 0; % Initialize turn lane states.TLCseq = 0;speed = s; % Set Speed to s input.initializeLJ(); % Initialize LabJackgtreset(); % Initialize global timerftreset(); % Initialize flicker timerend