function [Error, ljHandle] = initializeLJ()% Initializes the LabJack and loads all libraries% Should also globalize common LJ constants% Inputs: None% Outputs: [Error, ljHandle]% Declare global LabJack constantsglobal ljHandleglobal LJ_ioGET_AINglobal LJ_ioPUT_DACglobal LJ_ioPUT_DIGITAL_BITglobal LJ_ioGET_DIGITAL_BITljud_LoadDriverljud_Constants% Connect to the first LabJack[Error ljHandle] = ljud_OpenLabJack(LJ_dtU3,LJ_ctUSB,'1',1);% Set pin configurations to defaultError = ljud_ePut(ljHandle,LJ_ioPIN_CONFIGURATION_RESET,0,0,0);% Display any error messagesError_Message(Error)% Initialize Analog Input Pins (i.e. the photoresistor)% Replace 'analogPin' with correct pin numbersanalogPin = 6;for i = 1:length(analogPin)Error = ljud_ePut(ljHandle,LJ_ioPUT_ANALOG_ENABLE_BIT,analogPin(i),1,0);endend