function trafficArray = trafficLightsB()% Different Timing Signal than before (scenario B). Allows U-turns across divider.% Controls the timing for the traffic lights.% Inputs: speed (speed multiplier for pattern)% Outputs: trafficArray (data for lights)% initialize timerr = [1 0 0];y = [0 1 0];g = [0 0 1];b = [0 0 0];t = gtime(); % Call global time.global TLAseq % Call Global Turn Lane Variablesglobal TLCseq% Traffic Signal Timingif t < 15  TLAseq = 0;  if TLCseq == 1    trafficArray = [r r g g r];    shiftOut(trafficArray);  else    trafficArray = [r r r g r];    shiftOut(trafficArray);  endelseif t >= 15 && t < 21  if TLCseq == 1    trafficArray = [r r y y r];    shiftOut(trafficArray);  else    trafficArray = [r r r y r];    shiftOut(trafficArray);  endelseif t >= 21 && t < 36  TLCseq = 0;  trafficArray = [r r r r g];  shiftOut(trafficArray);elseif t >= 36 && t < 42  trafficArray = [r r r r y];  shiftOut(trafficArray);elseif t >= 42 && t < 54  if TLAseq == 1    trafficArray = [g g r r r];    shiftOut(trafficArray);  else    trafficArray = [r g r r r];    shiftOut(trafficArray);  endelseif t >= 54 && t < 60  if TLAseq == 1    trafficArray = [y y r r r];    shiftOut(trafficArray);  else    trafficArray = [r y r r r];    shiftOut(trafficArray);  endelseif t >= 60    trafficArray = [b b b b b]    gtreset() % If greater than 60, it  resets the time.else    trafficArray = [b b b b b];    shiftOut(trafficArray);endend %End of full function