function [Error, ljHandle] = initializeLJ()% Initializes the LabJack and loads all libraries% Should also globalize common LJ constants% Inputs: None% Outputs: [Error, ljHandle]ljud_LoadDriverljud_Constants[Error  ljHandle] = ljud_OpenLabJack(LJ_dtU3,LJ_ctUSB,'1',1);Error = ljud_ePut (ljHandle, LJ_ioPIN_CONFIGURATION_RESET,0,0,0);Error_Message(Error)% Initialize Analog Input Pins (i.e. the photoresistor)% Replace 'analogPin' with correct pin numbersError = ljud_ePut (ljHandle, LJ_ioPUT_ANALOG_ENABLE_BIT, analogPin, 1, 0);end